// src/components/Heatmap.js
import React, { useContext, useState, useEffect } from 'react';
import { Tooltip } from 'react-tooltip';
import { DataContext } from '../contexts/DataContext';

const Heatmap = () => {
  const { data } = useContext(DataContext);
  const [selectedMetric, setSelectedMetric] = useState(Object.keys(data[0] || {})[1]);

  useEffect(() => {
    Tooltip.rebuild();
  }, [data, selectedMetric]);

  const handleMetricChange = (event) => {
    setSelectedMetric(event.target.value);
  };

  const getColor = (value) => {
    const hue = (1 - value) * 240;
    return `hsl(${hue}, 100%, 50%)`;
  };

  return (
    <div>
      <h2>Heatmap</h2>
      {data.length > 0 && (
        <>
          <label>
            Select Metric:
            <select value={selectedMetric} onChange={handleMetricChange}>
              {Object.keys(data[0] || {}).slice(1).map((key) => (
                <option key={key} value={key}>
                  {key}
                </option>
              ))}
            </select>
          </label>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(24, 1fr)', gap: '1px' }}>
            {data.map((well, index) => (
              <div
                key={index}
                data-tooltip-id="tooltip"
                data-tooltip-content={Object.entries(well).map(([key, value]) => `${key}: ${value}`).join('\n')}
                style={{
                  backgroundColor: getColor(parseFloat(well[selectedMetric])),
                  width: '20px',
                  height: '20px',
                }}
              ></div>
            ))}
          </div>
          <Tooltip id="tooltip" place="top" effect="solid" multiline={true} />
        </>
      )}
    </div>
  );
};

export default Heatmap;
