import React, { useContext, useState } from 'react';
import Papa from 'papaparse';
import { DataContext } from '../contexts/DataContext';

const DataLoader = () => {
  const { setData } = useContext(DataContext);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        if (results.errors.length) {
          setError('Error parsing CSV file.');
          console.error(results.errors);
        } else {
          const requiredColumns = ['well'];
          const csvColumns = Object.keys(results.data[0]);

          const missingColumns = requiredColumns.filter(column => !csvColumns.includes(column));
          if (missingColumns.length) {
            setError(`CSV file is missing required columns: ${missingColumns.join(', ')}`);
          } else {
            setData(results.data);
            setError(null);
          }
        }
      },
    });
  };

  return (
    <div>
      <h2>Load Data</h2>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      {error && <div style={{ color: 'red' }}>{error}</div>}
    </div>
  );
};

export default DataLoader;
